  const user = { username: 'User123', password: 'Pass456' };

  // Sample messages data. In a real app, consider fetching messages based on the current channel
  const sampleMessages = {
    // Assuming channel.id is used as the key
    1: ['Welcome to Channel 1!', 'Message 2 in Channel 1'],
    2: ['Welcome to Channel 2!', 'Message 2 in Channel 2'],
    // Add more channels as needed
  };

  return (
    <div className="app">
      <ChannelList setCurrentChannel={setCurrentChannel} />
      {/* Ensure messages are displayed for the current channel. Adjust as needed for real data fetching */}
      <MessageList messages={sampleMessages[currentChannel] || []} />
    </div>
  );
}

