import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import ChannelList from './ChannelList';
import MessageList from './MessageList';
import ReplyList from './ReplyList';
import ProfilePage from './Profile';
import LoginPage from './LoginPage';
import ProtectedRoute from './ProtectedRoute';
import { useParams } from 'react-router-dom';
import './App.css'


// Create a component to handle channel and message display logic
function ChannelAndMessages() {
  let { channelId, messageId } = useParams(); // Grab channelId and messageId from URL
  const [selectedMessageId, setSelectedMessageId] = useState(null);

  useEffect(() => {
    // React to changes in messageId from the URL
    setSelectedMessageId(messageId);
  }, [messageId, channelId]); // React to changes in both messageId and channelId

  // Function to close the ReplyList and navigate back to the channel view
  const closeReplyList = () => setSelectedMessageId(null);

  return (
    <div className="channel-content">
      <ChannelList currentChannel={channelId} />
      <MessageList currentChannel={channelId} setSelectedMessageId={setSelectedMessageId} />
      {selectedMessageId && <ReplyList messageId={selectedMessageId} onClose={closeReplyList} />}
    </div>
  );
}

function App() {
  return (
    <Router>
      <div className="app">
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          <Route path="/profile" element={<ProtectedRoute><ProfilePage /></ProtectedRoute>} />
          <Route path="/channels/:channelId" element={<ProtectedRoute><ChannelAndMessages /></ProtectedRoute>} />
          <Route path="/channels/:channelId/replies/:messageId" element={<ProtectedRoute><ChannelAndMessages /></ProtectedRoute>} />
          <Route path="/" element={<Navigate replace to="/channels/1" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
