import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ChannelList from './ChannelList';
import MessageList from './MessageList';
import ProfilePage from './Profile';
import LoginPage from './LoginPage';
import './App.css'

function App() {
  const [currentChannel, setCurrentChannel] = useState('');
  const user = { username: 'User123', password: 'Pass456' };

  // Sample messages data
  const sampleMessages = {
    1: ['Welcome to Channel 1!', 'Message 2 in Channel 1'],
    2: ['Welcome to Channel 2!', 'Message 2 in Channel 2'],
    // Add more as needed
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={
          <div className="app">
            <ChannelList setCurrentChannel={setCurrentChannel} />
            <MessageList messages={sampleMessages[currentChannel] || []} />
          </div>
        } />
        <Route path="/profile" element={<ProfilePage user={user} />} />
        <Route path="/login" element={<LoginPage />} />
      </Routes>
    </Router>
  );
}

export default App;
