import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import ChannelList from './ChannelList';
import MessageList from './MessageList';
import ProfilePage from './Profile';
import LoginPage from './LoginPage';
import { useParams } from 'react-router-dom';
import './App.css'


function App() {
  // No longer maintaining currentChannel in the state here
  // const [currentChannel, setCurrentChannel] = useState('');

  return (
    <Router>
      <div className="app">
        <Routes>
          <Route path="/channels/:channelId" element={<ChannelAndMessages />} />
          <Route path="/profile" element={<ProfilePage />} />
          <Route path="/login" element={<LoginPage />} />
          {/* Redirect root path to a default or specific channel for better UX */}
          <Route path="/" element={<Navigate replace to="/channels/1" />} />
        </Routes>
      </div>
    </Router>
  );
}


// A component to handle rendering both the channel list and messages for a channel
function ChannelAndMessages() {
  let { channelId } = useParams(); // This hook allows you to grab params from the URL.

  return (
    <>
      <ChannelList />
      <MessageList currentChannel={channelId} /> {/* Pass channelId to MessageList */}
    </>
  );
}


export default App;
