import React, { useState, useEffect } from 'react';

function ReplyList({ messageId }) {
  const [parentMessage, setParentMessage] = useState(null);
  const [replies, setReplies] = useState([]);
  const [newReply, setNewReply] = useState(''); // State to hold the new reply input

  const apiKey = localStorage.getItem('api_key');

  useEffect(() => {
    if (!messageId) return;

    // Fetch the parent message and its replies
    fetch(`/api/messages/${messageId}/replies`, {
      headers: {
        'API-Key': apiKey // Assuming the API key is sent as a Bearer token
      },
    })
    .then(response => response.json())
    .then(data => {
      setParentMessage(data.parentMessage);
      setReplies(data.replies);
    })
    .catch(error => console.error('Error fetching replies:', error));
  }, [messageId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newReply.trim()) return; // Prevent sending empty replies

    try {
      const response = await fetch(`/api/messages/${messageId}/replies`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'API-Key': apiKey // Assuming the API key is sent as a Bearer token
        },
        body: JSON.stringify({ message: newReply }),
      });

      if (!response.ok) {
        throw new Error('Failed to post reply');
      }

      setNewReply(''); // Clear the input field on successful reply post

      // Optionally, refetch replies for the current message to show the new reply
    } catch (error) {
      console.error('Error posting reply:', error);
    }
  };

  return (
    <div className="reply-list">
      <div className="parent-message">
        <strong>{parentMessage?.name}:</strong> {parentMessage?.body}
      </div>
      <div className="replies">
        {replies.length > 0 ? (
          replies.map((reply, index) => (
            <div key={index} className="reply">
              <strong>{reply.name}:</strong> {reply.body}
            </div>
          ))
        ) : (
          <p>No replies yet</p>
        )}
      </div>
      <form onSubmit={handleSubmit} className="reply-form">
        <input
          type="text"
          value={newReply}
          onChange={(e) => setNewReply(e.target.value)}
          placeholder="Write a reply..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default ReplyList;
