import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

function ChannelList({}) {
  const [channels, setChannels] = useState([]);
  const [unreadMessages, setUnreadMessages] = useState({});
  const [selectedChannelId, setSelectedChannelId] = useState(null);
  const { channelId } = useParams();
  let navigate = useNavigate();

  const goToProfile = () => {navigate('/profile');};

  useEffect(() => {
    const apiKey = localStorage.getItem('api_key');

    // Fetch channels from the API
    fetch('/api/channels', {
        method: 'GET', 
        headers: {
            'API-Key': apiKey
        }
    }) // Adjust the URL as needed
      .then(response => response.json())
      .then(data => {
        setChannels(data);
      })
      .catch(error => console.error('Error fetching channels:', error));

      fetch('/api/channels/access', {
        method: 'GET',
        headers: {
            'API-Key': apiKey
        },
      })
      .then(response => response.json())
      .then(data => {
        const unreadCounts = {};
        data.forEach(channel => {
          unreadCounts[channel.channel_id] = channel.unread_messages;
        });
        setUnreadMessages(unreadCounts);
      })
      .catch(error => console.error('Error fetching unread messages:', error));
      if (channelId) {
        setSelectedChannelId(channelId);
      }

  }, [channelId]); // This effect runs once on component mount


  const selectChannel = async (channelId) => {
    // Assuming `apiKey` is available, for example, through context or state
    const apiKey = localStorage.getItem('api_key');

    try {
      // POST request to update last_message_seen
      const response = await fetch(`/api/channels/${channelId}/access`, {
        method: 'POST',
        headers: {
            'API-Key': apiKey
        }
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log(data); // Log the success message
      navigate(`/channels/${channelId}`); // Update the URL when a channel is clicked
      // Proceed to update the UI to show the selected channel
      // This might involve setting state or navigating to a different component/route
    } catch (error) {
      console.error('Error updating last message seen:', error);
    }
    setSelectedChannelId(channelId);
  };


  const fetchChannels = () => {
    const apiKey = localStorage.getItem('api_key');
    fetch('/api/channels', {
        method: 'GET', 
      headers: {
        'API-Key': apiKey
      },
    })
    .then(response => response.json())
    .then(data => setChannels(data))
    .catch(error => console.error('Error fetching channels:', error));
  };

  const handleCreateChannel = async () => {
    const channelName = prompt('Enter new channel name:');
    if (!channelName) return; // Exit if the user cancels or enters an empty name

    const apiKey = localStorage.getItem('api_key'); // Retrieve API key from localStorage

    try {
      const response = await fetch('/api/channels/new', { // Adjust URL as needed
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'API-Key': apiKey
        },
        body: JSON.stringify({ name: channelName }),
      });

      if (!response.ok) {
        throw new Error('Failed to create channel');
      }
      fetchChannels();
      // Optionally, fetch channels again to update the list or update state directly
    } catch (error) {
      console.error('Error creating channel:', error);
    }
  };


  return (
    <div className="channel-list">
      <h2 className="channel-header">Channels</h2>
      {channels.map(channel => (
        <div className={`channel-item ${channel.id === selectedChannelId ? 'active' : ''}`} key={channel.id} onClick={() => selectChannel(channel.id)}>
          {channel.name}
          {unreadMessages[channel.id] ? ` (${unreadMessages[channel.id]} unread)` : ''}
        </div>
      ))}
      <button onClick={handleCreateChannel}>Create New Channel</button>
      <button onClick={goToProfile}>Go to Profile</button>
    </div>
  );
}

export default ChannelList;
