import React, { useState, useEffect } from 'react';

function MessageList({ currentChannel, setSelectedMessageId }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [hoveredMessageId, setHoveredMessageId] = useState(null);
  const [showReactionMenuForMessageId, setShowReactionMenuForMessageId] = useState(null);

  const apiKey = localStorage.getItem('api_key'); // Assuming the API key is stored in localStorage

  useEffect(() => {
    if (!currentChannel) return; // Don't fetch if no channel is selected

    fetch(`/api/channels/${currentChannel}/messages`, {
      method: 'GET',
      headers: {
        'API-Key': apiKey // Assuming the API key is sent as a Bearer token
      },
    })
    .then(response => response.json())
    .then(data => {
      setMessages(data);
    })
    .catch(error => console.error('Error fetching messages:', error));
  }, [currentChannel]); // Refetch messages when currentChannel changes


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!newMessage.trim()) return; // Prevent sending empty messages

    try {
      const response = await fetch(`/api/channels/${currentChannel}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'API-Key': apiKey // Assuming the API key is sent as a Bearer token
        },
        body: JSON.stringify({ message: newMessage }),
      });

      if (!response.ok) {
        throw new Error('Failed to post message');
      }

      setNewMessage(''); // Clear the input field on successful message post

      // Consider refetching messages for the current channel to show the new message
    } catch (error) {
      console.error('Error posting message:', error);
    }
  };

  const handleReplyClick = (messageId) => {
    // Set the selected message ID to open the replies panel
    setSelectedMessageId(messageId);
  };

  const handleAddReaction = (messageId, emoji) => {
    console.log(`Adding '${emoji}' reaction to message ${messageId}`);
    // Send the emoji reaction to your backend
    setShowReactionMenuForMessageId(null); // Optionally close the menu
  };

  return (
    <div className="message-list">
      {messages.map((msg) => (
        <div 
          key={msg.id} 
          className="message-item"
          onMouseEnter={() => setHoveredMessageId(msg.id)}
          onMouseLeave={() => setHoveredMessageId(null)}
        >
          <strong>{msg.name}:</strong> {msg.body}
          {msg.num_replies > 0 && (
            <div onClick={() => handleReplyClick(msg.id)}>
              {msg.num_replies} of replies
            </div>
          )}
          {hoveredMessageId === msg.id && (
            <div className="message-actions">
              <button onClick={() => handleReplyClick(msg.id)}>Reply to Thread</button>
              {/* Adjusted "Add Reaction" button */}
              <button onClick={() => setShowReactionMenuForMessageId(showReactionMenuForMessageId === msg.id ? null : msg.id)}>Add Reaction</button>
              {showReactionMenuForMessageId === msg.id && (
                <div className="reaction-menu">
                  <button onClick={() => handleAddReaction(msg.id, '‚ù§Ô∏è')}>‚ù§Ô∏è</button>
                  <button onClick={() => handleAddReaction(msg.id, 'üòä')}>üòä</button>
                  <button onClick={() => handleAddReaction(msg.id, 'üò¢')}>üò¢</button>
                </div>
              )}
            </div>
          )}
        </div>
      ))}
      {/* Form to submit a new message */}
    </div>
  );
}


export default MessageList;
