import React, { useState } from 'react';
import ChannelList from './ChannelList';
import MessageList from './MessageList';
import './App.css'

// Sample data
const sampleChannels = ['general', 'random', 'help'];
const sampleMessages = {
  general: ['Welcome to general!', 'General message 2'],
  random: ['Random thought...', 'Another random message'],
  help: ['Need help?', 'Post your questions here.']
};

function App() {
  const [currentChannel, setCurrentChannel] = useState('general');

  return (
    <div className="app">
      <ChannelList channels={sampleChannels} setCurrentChannel={setCurrentChannel} />
      <MessageList messages={sampleMessages[currentChannel]} />
    </div>
  );
}

export default App;
