import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ProfilePage() {
    const navigate = useNavigate();
    const [newUsername, setNewUsername] = useState('');
    const [newPassword, setNewPassword] = useState('');

    const apiKey = localStorage.getItem('api_key'); // Assuming the API key is stored in localStorage
    const username = sessionStorage.getItem('username');
    const password = sessionStorage.getItem('password');

    const handleLogout = () => {
        // Clear sessionStorage and localStorage
        sessionStorage.removeItem('username');
        sessionStorage.removeItem('password');
        localStorage.removeItem('api_key');

        // Redirect to login page
        navigate('/login');
    };

    const handleChangeUsername = async (e) => {
        e.preventDefault();
        try {
        const response = await fetch('/api/user/username', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'API-Key': apiKey
            }, 
            body: JSON.stringify({ new_username: newUsername }),
        });

        if (!response.ok) {
            throw new Error('Failed to update username');
        }

        const data = await response.json();
        console.log(data.message);
        // Update sessionStorage with the new username
        sessionStorage.setItem('username', newUsername);
        // Optionally, inform the user of success
        } catch (error) {
        console.error('Error updating username:', error);
        // Optionally, handle/display the error to the user
        }
    };

    const handleChangePassword = async (e) => {
        e.preventDefault();
        try {
        const response = await fetch('/api/user/password', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'API-Key': apiKey
            }, 
            body: JSON.stringify({ new_password: newPassword }),
        });

        if (!response.ok) {
            throw new Error('Failed to update password');
        }

        const data = await response.json();
        console.log(data.message);
        // Update sessionStorage with the new password
        sessionStorage.setItem('password', newPassword);
        // Optionally, inform the user of success
        } catch (error) {
        console.error('Error updating password:', error);
        // Optionally, handle/display the error to the user
        }
    };

    return (
        <div>
        <h2>Profile Page</h2>
        <form onSubmit={handleChangeUsername}>
            <input
            type="text"
            value={newUsername}
            onChange={(e) => setNewUsername(e.target.value)}
            placeholder={username}
            required
            />
            <button type="submit">Change Username</button>
        </form>

        <form onSubmit={handleChangePassword}>
            <input
            type="text"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder={password}
            required
            />
            <button type="submit">Change Password</button>
        </form>
        <button onClick={handleLogout}>Log Out</button>
        </div>
    );
}

export default ProfilePage;
