import React, { useState, useEffect } from 'react';

function MessageList({ currentChannel, setSelectedMessageId }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const apiKey = localStorage.getItem('api_key'); // Assuming the API key is stored in localStorage

  useEffect(() => {
    if (!currentChannel) return; // Don't fetch if no channel is selected

    fetch(`/api/channels/${currentChannel}/messages`, {
      method: 'GET',
      headers: {
        'API-Key': apiKey // Assuming the API key is sent as a Bearer token
      },
    })
    .then(response => response.json())
    .then(data => {
      setMessages(data);
    })
    .catch(error => console.error('Error fetching messages:', error));
  }, [currentChannel]); // Refetch messages when currentChannel changes

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!newMessage.trim()) return; // Prevent sending empty messages

    try {
      const response = await fetch(`/api/channels/${currentChannel}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${apiKey}`, // Ensure this matches how your API expects the token
        },
        body: JSON.stringify({ message: newMessage }),
      });

      if (!response.ok) {
        throw new Error('Failed to post message');
      }

      setNewMessage(''); // Clear the input field on successful message post

      // Consider refetching messages for the current channel to show the new message
    } catch (error) {
      console.error('Error posting message:', error);
    }
  };

  return (
    <div className="message-list">
      {messages.map((msg, index) => (
        <div key={index} className="message-item">
          <strong>{msg.name}:</strong> {msg.body}
          <button onClick={() => setSelectedMessageId(msg.id)}>See Replies</button>
        </div>
      ))}
      <form onSubmit={handleSubmit} className="message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Write a message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default MessageList;
