import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import ChannelList from './ChannelList';
import MessageList from './MessageList';
import ReplyList from './ReplyList';
import ProfilePage from './Profile';
import LoginPage from './LoginPage';
import ProtectedRoute from './ProtectedRoute';
import { useParams } from 'react-router-dom';
import './App.css'


// Create a component to handle channel and message display logic
function ChannelAndMessages() {
  let { channelId, messageId } = useParams();
  const [selectedMessageId, setSelectedMessageId] = useState(messageId || null);
  const [isNarrowScreen, setIsNarrowScreen] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => setIsNarrowScreen(window.innerWidth < 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    setSelectedMessageId(messageId);
    // Automatically adjust the view based on screen width and URL parameters
    if (isNarrowScreen) {
      if (messageId) setCurrentView('replies');
      else if (channelId) setCurrentView('messages');
      else setCurrentView('channels');
    }
  }, [messageId, channelId, isNarrowScreen]);

  const closeReplyList = () => setSelectedMessageId(null);

  const renderComponent = () => {
    if (isNarrowScreen) {
      switch (currentView) {
        case 'channels':
          return <ChannelList setCurrentView={setCurrentView} />;
        case 'messages':
          return <MessageList currentChannel={channelId} setSelectedMessageId={setSelectedMessageId} />;
        case 'replies':
          if (selectedMessageId) {
            return <ReplyList messageId={selectedMessageId} onClose={() => setCurrentView('messages')} />;
          }
          break;
        default:
          return <ChannelList setCurrentView={setCurrentView} />;
      }
    } else {
      // Wide screen layout
      return (
        <>
          <ChannelList setCurrentView={setCurrentView} />
          <MessageList currentChannel={channelId} setSelectedMessageId={setSelectedMessageId} />
          {selectedMessageId && <ReplyList messageId={selectedMessageId} onClose={closeReplyList} />}
        </>
      );
    }
  };

  return (
    <div className="channel-content">
      {renderComponent()}
    </div>
  );
}


function App() {
  return (
    <Router>
      <div className="app">
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          <Route path="/profile" element={<ProtectedRoute><ProfilePage /></ProtectedRoute>} />
          <Route path="/channels/:channelId" element={<ProtectedRoute><ChannelAndMessages /></ProtectedRoute>} />
          <Route path="/channels/:channelId/replies/:messageId" element={<ProtectedRoute><ChannelAndMessages /></ProtectedRoute>} />
          <Route path="/" element={<Navigate replace to="/channels/1" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
