import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import ChannelList from './ChannelList';
import MessageList from './MessageList';
import ReplyList from './ReplyList';
import ProfilePage from './Profile';
import LoginPage from './LoginPage';
import ProtectedRoute from './ProtectedRoute';
import { useParams } from 'react-router-dom';
import './App.css'


function App() {
  const [selectedMessageId, setSelectedMessageId] = useState(null);

  return (
    <Router>
      <div className="app">
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          <Route path="/profile" element={
            <ProtectedRoute>
              <ProfilePage />
            </ProtectedRoute>
          } />
          <Route path="/channels/:channelId" element={
            <ProtectedRoute>
            <div className="channel-content">
              <ChannelList />
              <MessageList setSelectedMessageId={setSelectedMessageId} /> {/* Pass setSelectedMessageId as a prop */}
              {selectedMessageId && <ReplyList messageId={selectedMessageId} />} {/* Conditionally render ReplyList */}
            </div>
          </ProtectedRoute>
          } />
          {/* Redirect root path to a default or specific channel for better UX */}
          <Route path="/" element={<Navigate replace to="/channels/1" />} />
        </Routes>
      </div>
    </Router>
  );
}


// A component to handle rendering both the channel list and messages for a channel
function ChannelAndMessages() {
  let { channelId } = useParams(); // This hook allows you to grab params from the URL.

  return (
    <>
      <ChannelList />
      <MessageList currentChannel={channelId} /> {/* Pass channelId to MessageList */}
    </>
  );
}


export default App;
