import React, { useState, useEffect } from 'react';

function ReplyList({ messageId }) {
  const [parentMessage, setParentMessage] = useState(null);
  const [replies, setReplies] = useState([]);

  useEffect(() => {
    if (!messageId) return;

    const apiKey = localStorage.getItem('api_key');

    // Fetch the parent message and its replies
    // Assuming an endpoint like /api/messages/:messageId/replies
    fetch(`/api/messages/${messageId}/replies`, {
      headers: {
        'API-Key': apiKey // Assuming the API key is sent as a Bearer token
      },
    })
    .then(response => response.json())
    .then(data => {
      setParentMessage(data.parentMessage);
      setReplies(data.replies);
    })
    .catch(error => console.error('Error fetching replies:', error));
  }, [messageId]);

  if (!parentMessage) return null;

  return (
    <div className="reply-list">
      <div className="parent-message">
        <strong>Parent Message:</strong> {parentMessage.body}
      </div>
      <div className="replies">
        {replies.length > 0 ? (
          replies.map((reply, index) => (
            <div key={index} className="reply">
              {reply.body}
            </div>
          ))
        ) : (
          <p>No replies yet</p>
        )}
      </div>
    </div>
  );
}

export default ReplyList;
