import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const [isCreatingAccount, setIsCreatingAccount] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  let navigate = useNavigate();

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    // Placeholder for API call to login
    console.log('Logging in with:', username, password);
    // On successful login, API will return an apiKey
    // localStorage.setItem('apiKey', 'API_KEY_FROM_RESPONSE');
    // navigate('/'); // Navigate to the main page or wherever appropriate
  };

  const handleCreateAccountSubmit = async (e) => {
    e.preventDefault();
    // Assuming your API does not require username/password for signup, but adjust if it does
    try {
      const response = await fetch('/api/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // Uncomment and adjust the body if your API requires it
        // body: JSON.stringify({ username, password }), 
      });
      if (!response.ok) throw new Error('Failed to create account');

      const data = await response.json();
      localStorage.setItem('api_key', data.api_key); // Store API key in localStorage
      console.log('Account created:', data);
      navigate('/profile'); // Navigate to profile page upon successful account creation
    } catch (error) {
      console.error('Error creating account:', error);
      // Handle error (e.g., show error message to user)
    }
  };

  return (
    <div>
      <h2>{isCreatingAccount ? 'Create Account' : 'Login'}</h2>
      <form onSubmit={isCreatingAccount ? handleCreateAccountSubmit : handleLoginSubmit}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          required
        />
        <input
          type="text"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <button type="submit">{isCreatingAccount ? 'Create Account' : 'Login'}</button>
      </form>
      <button onClick={() => setIsCreatingAccount(!isCreatingAccount)}>
        {isCreatingAccount ? 'Already have an account? Log In' : 'Need an account? Create one'}
      </button>
    </div>
  );
}

export default LoginPage;
