import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function LoginPage() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    let navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
        const response = await fetch('/api/login', {
            method: 'GET',
            headers: {
            'Username': username,
            'Password': password,
            },
        });

        if (!response.ok) {
            throw new Error('User not found');
        }

        const { api_key } = await response.json();
        localStorage.setItem('api_key', api_key); 
        sessionStorage.setItem('username', username);
        sessionStorage.setItem('password', password);

        console.log('Login successful:', api_key);
        navigate('/profile'); // Navigate to profile page upon successful login
        } catch (error) {
        console.error('Error during login:', error);
        // Optionally handle the error, e.g., display an error message
        }
    };

    const handleCreateAccountSubmit = async (e) => {
        e.preventDefault();
        // Assuming your API does not require username/password for signup, but adjust if it does
        try {
        const response = await fetch('/api/signup', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            // Uncomment and adjust the body if your API requires it
            // body: JSON.stringify({ username, password }), 
        });
        if (!response.ok) throw new Error('Failed to create account');

        const data = await response.json();
        localStorage.setItem('api_key', data.api_key); // Store API key in localStorage
        sessionStorage.setItem('username', data.username); // Store username in sessionStorage
        sessionStorage.setItem('password', data.password); // Store password in sessionStorage
        console.log('Account created:', data);
        navigate('/profile'); // Navigate to profile page upon successful account creation
        } catch (error) {
        console.error('Error creating account:', error);
        // Handle error (e.g., show error message to user)
        }
    };

    return (
        <div>
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
            <button type="submit">Login</button>
        </form>
        <h2>Or</h2>
        <button onClick={handleCreateAccountSubmit}>Create an Account</button>
        </div>
    );
}

export default LoginPage;
