import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ChannelList({ setCurrentChannel }) {
  const [channels, setChannels] = useState([]);
  let navigate = useNavigate();

  const goToProfile = () => {navigate('/profile');};

  useEffect(() => {
    const apiKey = localStorage.getItem('api_key');

    // Fetch channels from the API
    fetch('/api/channels', {
        method: 'GET', 
        headers: {
            'API-Key': apiKey
        }
    }) // Adjust the URL as needed
      .then(response => response.json())
      .then(data => {
        setChannels(data);
      })
      .catch(error => console.error('Error fetching channels:', error));
  }, []); // This effect runs once on component mount

  const selectChannel = (channelId) => {
    navigate(`/channels/${channelId}`); // Update the URL when a channel is clicked
  };

  const handleCreateChannel = async () => {
    const channelName = prompt('Enter new channel name:');
    if (!channelName) return; // Exit if the user cancels or enters an empty name

    const apiKey = localStorage.getItem('api_key'); // Retrieve API key from localStorage

    try {
      const response = await fetch('/api/channels', { // Adjust URL as needed
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'API-Key': apiKey
        },
        body: JSON.stringify({ name: channelName }),
      });

      if (!response.ok) {
        throw new Error('Failed to create channel');
      }

      // Optionally, fetch channels again to update the list or update state directly
    } catch (error) {
      console.error('Error creating channel:', error);
    }
  };


  return (
    <div className="channel-list">
      <h2 className="channel-header">Channels</h2>
      {channels.map(channel => (
        <div key={channel.id} onClick={() => selectChannel(channel.id)}>
          {channel.name}
        </div>
      ))}
      <button onClick={handleCreateChannel}>Create New Channel</button>
      <button onClick={goToProfile}>Go to Profile</button>
    </div>
  );
}

export default ChannelList;
